import VR import math
class monitorStatusController: def __init__(self): print "initialisiere monitorStatusController" self.LagerCapacity = 0 self.LagerStand = 0 self.FertigCapacity = 0 self.FertigStand = 0 self.BoxVorFraese = 0 self.BoxNachFraese = 0 self.FraeseStatus = "Steht still" self.updateHomepage()
def updateHomepage(self): url = ("http://localhost:5500/monitorStatusWebsite?" + "lagerCapacity=" + str(self.LagerCapacity) + "&" + "lagerStand=" + str(self.LagerStand) + "&" + "fertigCapacity=" + str(self.FertigCapacity) + "&" + "fertigStand=" + str(self.FertigStand) + "&" + "fraeseStatus=" + str(self.FraeseStatus) ) VR.monitorStatusWeb.open(url)
def setFraeseStatus(self, status): self.FraeseStatus = status self.updateHomepage()
def setLagerCapacity(self, amount): self.LagerCapacity = amount self.updateHomepage()
def setLagerStand(self, amount): self.LagerStand = amount self.updateHomepage()
def setFertigCapacity(self, amount): self.FertigCapacity = amount self.updateHomepage()
def setFertigStand(self, amount): self.FertigStand = amount self.updateHomepage()
def destroy(self): return
if hasattr(VR, 'monitorStatusController') and VR.monitorStatusController is not None: VR.monitorStatusController.destroy() VR.monitorStatusController = monitorStatusController()
